Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('599','0','Qué es','Portal sostenido por el Comité Árabe contra la Difamación que pretende ofrecer el punto de vista de la propia comunidad árabe sobre su realidad política y social, así como dar a conocer la lengua y la cultura árabe.',null);
