Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('603','0','Qué es','Medio de información alternativa que publica noticias que no son consideradas importantes por los medios de comunicación tradicionales, dándoles un tratamiento diferente, desde una perspectiva de transformación social, en una línea más justa, igualitaria y equilibrada social y ecológicamente. <br>',null);
