Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3520','0',null,'<p>&nbsp;&nbsp;&nbsp;&nbsp; El Ministerio de Educaci&oacute;n es encargado de: proponer y evaluar las pol&iacute;ticas y los planes de desarrollo educacional y cultural; asignar los recursos necesarios para el desarrollo de las actividades educacionales y de extensi&oacute;n cultural y evaluar el desarrollo de la educaci&oacute;n como un proceso integral, informando de sus resultados a la comunidad. El Ministerio realiza sus funciones de administraci&oacute;n de la educaci&oacute;n en tres niveles: nacional, regional (por las&nbsp; Secretar&iacute;as Regionales Ministeriales) y provincial (mediante los Consejos Provinciales de Educaci&oacute;n).</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp; Es funci&oacute;n de las Municipalidades gestionar los centros educativos p&uacute;blicos de educaci&oacute;n b&aacute;sica y media. La direcci&oacute;n de los centros est&aacute; a cargo de un/a director/a, que adem&aacute;s es el responsable de planificar, organizar, ejecutar, supervisar y evaluar las actividades relacionadas con el desarrollo del proceso de aprendizaje.<br /><br />&nbsp;&nbsp;&nbsp;&nbsp; En cada centro se crear&aacute; el Consejo de Profesores formado por el personal docente directivo, t&eacute;cnico- pedag&oacute;gico y docente de aula. Tambi&eacute;n, y con el objetivo de colaborar en los prop&oacute;sitos educativos del centro, existe el Consejo de Padres y Apoderados. El Centro de Alumnos se integra por los alumnos, y se define como una organizaci&oacute;n cuya finalidad es servir a sus miembros como medio de desarrollar en ellos el pensamiento reflexivo, juicio cr&iacute;tico y la voluntad de acci&oacute;n.<br /><br /><br /></p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3521','0',null,'&nbsp;&nbsp;&nbsp;- El acceso a la <strong>Educaci&oacute;n Superior</strong> tiene como requisito m&iacute;nimo estar en posesi&oacute;n&nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp; de&nbsp;la Licencia de Educaci&oacute;n Media. Existen tres tipos de instituciones de educaci&oacute;n&nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;superior: las universidades, los institutos profesionales y los centros de formaci&oacute;n&nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; t&eacute;cnica.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3522','0',null,'&nbsp;&nbsp; - La <strong>Educaci&oacute;n Media</strong> tiene una duraci&oacute;n de cuatro a&ntilde;os, la edad l&iacute;mite para el&nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; acceso es de 18 a&ntilde;os. Est&aacute; compuesta por dos modalidades:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; -&nbsp; la <em>Human&iacute;stico-Cient&iacute;fica</em> (EMHC): se divide en dos ciclos, y comprende&nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; asignaturas que constituyen una ense&ntilde;anza de car&aacute;cter general:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; -&nbsp; <em>Primer ciclo</em>: corresponde a los cursos primero y segundo; <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - <em>Segundo ciclo</em>: formado por el tercero y cuarto curso. <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Al finalizar este nivel, los alumnos obtienen la Licencia de Educaci&oacute;n Media&nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; (LEM).<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - la <em>T&eacute;cnico-Profesional</em> (EMTP): est&aacute; compuesta por cinco ramas, a saber:&nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Comercial, Industrial, T&eacute;cnica, Agr&iacute;cola, y Mar&iacute;tima. Adem&aacute;s de obtener su&nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; LEM una vez finalizados sus estudios, los alumnos pueden acceder al t&iacute;tulo&nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; de t&eacute;cnico de nivel medio en la especialidad que hayan elegido al interior de&nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; cada rama.<br />',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3523','0',null,'&nbsp;&nbsp; - La <strong>Educaci&oacute;n General B&aacute;sica (EGB)</strong> es obligatoria y tiene una duraci&oacute;n de ocho&nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp; a&ntilde;os.&nbsp;La EGB est&aacute; dividida en dos ciclos: <br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - <em>Primer Ciclo</em>: comprende los primeros cuatro grados. Se aplica, de preferencia&nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; una metodolog&iacute;a globalizada, con un solo profesor.<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - <em>Segundo Ciclo</em>: de cuatro grados y dividido en dos subciclos: 5&ordm;-6&ordm; y 7&ordm;-8&ordm;. La&nbsp;&nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; promoci&oacute;n del 1&ordm; al 2&ordm; a&ntilde;o exige s&oacute;lo haber asistido al 85% de las clases. Los&nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; contenidos se organizan por materias que son impartidas por especialistas.<br /><br />',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3122','0',null,'<br /><strong>3 &ndash; Incremento de la Matr&iacute;cula en el Bachillerato hasta alcanzar al menos el 75% de la poblaci&oacute;n en la edad correspondiente.<br /><br />Objetivo</strong>: Formar j&oacute;venes competentes, con enfoque intercultural inclusivo y equitativo, que les posibilite continuar con los estudios superiores e incorporarse a la vida productiva, conscientes de su identidad nacional, con enfoque pluricultural y multi&eacute;tnico, en el marco de respeto a los derechos humanos y colectivos, la naturaleza y la vida.<br /><br /><strong>Principales l&iacute;neas de acci&oacute;n</strong>:<br />-&nbsp;Construcci&oacute;n, implementaci&oacute;n e interculturalizaci&oacute;n del nuevo modelo educativo para el bachillerato general y t&eacute;cnico, en articulaci&oacute;n con la educaci&oacute;n b&aacute;sica y superior del sistema hispano biling&uuml;e.<br />-&nbsp;Determinaci&oacute;n de modelos educativos que desarrollen competencias de emprendimiento a trav&eacute;s de la vinculaci&oacute;n de la educaci&oacute;n y el trabajo productivo.<br />',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3123','0',null,'<br /><strong>2 &ndash; Universalizaci&oacute;n de la Educaci&oacute;n General B&aacute;sica de primero a d&eacute;cimo a&ntilde;os.<br /><br />Objetivo</strong>: Brindar educaci&oacute;n de calidad con enfoque inclusivo y de equidad, a todos los ni&ntilde;os y ni&ntilde;as, para que desarrollen sus competencias de manera integral y se conviertan en ciudadanos positivos, activos, capaces de preservar ambiente cultural y respetuosos de la pluricultural y multiling&uuml;ismo.<br /><br /><strong>Principales l&iacute;neas de acci&oacute;n</strong>:<br />-&nbsp;Articulaci&oacute;n con la educaci&oacute;n inicial y el bachillerato, en el marco de la atenci&oacute;n a la diversidad, la inclusi&oacute;n educativa, el desarrollo y difusi&oacute;n cultural, la identidad pluricultural y multi&eacute;tnica y la preservaci&oacute;n del medio ambiente.<br />-&nbsp;Eliminaci&oacute;n de barreras de ingreso al sistema fiscal de educaci&oacute;n garantizando la gratuidad de la ense&ntilde;anza.<br />-&nbsp;Incremento de la tasa de retenci&oacute;n, garantizando adem&aacute;s la alimentaci&oacute;n escolar.<br />',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3124','0',null,'<br /><strong>1 &ndash; Pol&iacute;tica: Universalizaci&oacute;n de la Educaci&oacute;n Inicial de 0 a 5 a&ntilde;os de edad</strong>.<br /><br /><strong>Objetivo</strong>: Brindar educaci&oacute;n inicial para ni&ntilde;as y ni&ntilde;os menores de 5 a&ntilde;os, equitativa y de calidad que garantice y respete sus derechos, la diversidad cultural y ling&uuml;&iacute;stica, el ritmo natural de crecimiento y aprendizaje y fomente valores fundamentales, incorporando a la familia y a la comunidad, en el marco de una concepci&oacute;n inclusiva.<br /><br /><strong>Principales l&iacute;neas de acci&oacute;n</strong>:<br />-&nbsp;Rector&iacute;a del Ministerio de Educaci&oacute;n los subsistemas de educaci&oacute;n hispano biling&uuml;e en las diferentes modalidades del nivel.<br />-&nbsp;Articulaci&oacute;n de la educaci&oacute;n inicial con la educaci&oacute;n general b&aacute;sica.<br />-&nbsp;Inclusi&oacute;n y ampliaci&oacute;n de cobertura educativa en el nivel de educaci&oacute;n inicial.<br />-&nbsp;Implementaci&oacute;n de educaci&oacute;n infantil, familiar comunitaria o intercultural biling&uuml;e.<br />',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('182','0','p_01','<p>La direcci&oacute;n y administraci&oacute;n del servicio educativo estatal en Colombia se realiza de forma descentralizada y es competencia de la Naci&oacute;n y las entidades territoriales, esto es, los departamentos y municipios certificados.<br /><br />Sobre la base de la Ley 715 de 2001, y en el marco del Decreto 5012 de 2009, le compete al Ministerio de Educaci&oacute;n Nacional (MEN) una serie de funciones, entre las que se destacan:<br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - Establecer pol&iacute;ticas para dotar al sistema educativo de un servicio de calidad con acceso equitativo y con permanencia en el sistema, dise&ntilde;ar est&aacute;ndares de calidad en cuanto a servicios y contenidos, y garantizar y promover el derecho y acceso a la educaci&oacute;n p&uacute;blica en todos los niveles.&nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - Generar directrices, efectuar seguimiento y apoyar a las entidades territoriales para una adecuada gesti&oacute;n de los rescursos humanos del sector educativo.&nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - Orientar la educaci&oacute;n superior en el marco de la autonom&iacute;a universitaria.<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - Velar por la calidad de la educaci&oacute;n mediante las funciones de regulaci&oacute;n, inspecci&oacute;n y vigilancia.<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - Asesorar a los Departamentos, Municipios y Distritos.</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('184','0','p_01','<p>En base al Art&iacute;culo 5 de Ley General de Educaci&oacute;n, la educaci&oacute;n en Colombia tiene los fines siguientes:<br />&nbsp;- El pleno desarrollo de la personalidad sin m&aacute;s limitaciones que las que le imponen los derechos de los dem&aacute;s y el orden jur&iacute;dico, dentro de un proceso de formaci&oacute;n integra, f&iacute;sica, ps&iacute;quica, intelectual, moral, espiritual, social, afectiva, &eacute;tica, c&iacute;vica y dem&aacute;s valores humanos.</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('185','0','p_09','<p>El art&iacute;culo 20 de Ley General de Educaci&oacute;n define los objetivos de la educaci&oacute;n b&aacute;sica de la siguiente manera:<br />&nbsp;- Propiciar una formaci&oacute;n general mediante el acceso, de manera cr&iacute;tica y creativa, al conocimiento cient&iacute;fico, tecnol&oacute;gico, art&iacute;stico y human&iacute;stico y de sus relaciones con la vida social y con la naturaleza, de manera tal que prepare al educando para los niveles superiores del proceso educativo y para su vinculaci&oacute;n con la sociedad y el trabajo.</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3582','0',null,'<strong>Ley Org&aacute;nica de Educaci&oacute;n 79/1966</strong>, regula la educaci&oacute;n en los niveles preescolar, primario, medio y magisterial.<br /><br /><strong>Ley Org&aacute;nica de la Universidad Nacional Aut&oacute;noma de Honduras</strong> de 1957 y <strong>Ley de Educaci&oacute;n Superior</strong> de 1989, regulan la educaci&oacute;n superior.<br /><br /><strong>Ley del Instituto Nacional de Previsi&oacute;n del Magisterio</strong> (INPREMA) y <strong>Ley del Estatuto del Docente Hondure&ntilde;o</strong>, reglamentan el ejercicio docente.<br /><br /><strong>Ley de Rehabilitaci&oacute;n de la Persona Minusv&aacute;lida</strong> de 1987, regula la Educaci&oacute;n especial.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3583','0',null,'<br />La Direcci&oacute;n General de Alfabetizaci&oacute;n y Educaci&oacute;n de Adultos (conocida a partir de la reestructuraci&oacute;n del Ministerio de Educaci&oacute;n de 1997 como la <strong>Direcci&oacute;n General de Educaci&oacute;n Continua</strong>) tiene como finalidad atender la poblaci&oacute;n mayor de 14 a&ntilde;os que ha estado al margen de la educaci&oacute;n escolarizada.<br /><br />La Secretar&iacute;a de Educaci&oacute;n, dando cumplimiento al Programa de Modernizaci&oacute;n del Estado en su &aacute;mbito sectorial, ha iniciado un proceso para su propia reorganizaci&oacute;n y desconcentraci&oacute;n, con el prop&oacute;sito principal de alcanzar un mejor desempe&ntilde;o individual y colectivo en la prestaci&oacute;n de los servicios educativos.<br /><br />La reorganizaci&oacute;n y desconcentraci&oacute;n de la Secretar&iacute;a de Educaci&oacute;n se est&aacute; llevando a cabo por etapas, existiendo en la estructura propuesta una intenci&oacute;n deliberada de delegar funciones a las diferentes instancias operativas de la instituci&oacute;n y transferir hacia los departamentos, distritos y centros educativos varias tareas que hasta ahora han sido exclusividad del nivel central. Consecuentemente, se est&aacute; adoptando gradualmente una estructura funcional y organizativa compuesta por tres instancias: el despacho del titular (Secretario de Estado), encargado de conducir la instituci&oacute;n y el conjunto de unidades centrales de apoyo a la conducci&oacute;n; Dos Subsecretar&iacute;as, una que conducir&aacute; los aspectos pedag&oacute;gicos y otra encargada de los aspectos administrativos; y las Direcciones Departamentales y Distritales de Educaci&oacute;n y centros educativos encargados de la operatividad del sistema.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3584','0',null,'El Estado cumple su responsabilidad con la educaci&oacute;n en los diferentes niveles por medio de dos entidades gubernamentales: una centralizada, la Secretar&iacute;a de Educaci&oacute;n P&uacute;blica, y otra descentralizada, la Universidad Nacional Aut&oacute;noma de Honduras, cada una con sus competencias bien delimitadas.<br /><br />La <strong>Secretar&iacute;a de Educaci&oacute;n</strong> (anteriormente, la Secretar&iacute;a de Educaci&oacute;n P&uacute;blica &ndash;SEP) tiene la responsabilidad de definir e implementar pol&iacute;ticas y estrategias en materia de educaci&oacute;n, administrar el sistema y buscar los mecanismos de coordinaci&oacute;n para la ejecuci&oacute;n de acciones educativas enmarcadas dentro de la planificaci&oacute;n sectorial y nacional. Por otra parte, el sistema de educaci&oacute;n superior es competencia de la <strong>Universidad Nacional Aut&oacute;noma de Honduras (UNAH)</strong>.<br /><br />La SEP ejecuta sus funciones a trav&eacute;s de los programas siguientes: educaci&oacute;n primaria, educaci&oacute;n media, educaci&oacute;n de adultos, direcci&oacute;n sectorial y administraci&oacute;n central.<br /><br />La Direcci&oacute;n Sectorial y la Administraci&oacute;n Central dirigen la pol&iacute;tica educativa del pa&iacute;s, establecen los mecanismos administrativos para el logro de los objetivos de cada uno de los programas, racionalizan los recursos y coordinan la participaci&oacute;n tanto p&uacute;blica como privada. Tienen tambi&eacute;n a su cargo el dise&ntilde;o curricular, la evaluaci&oacute;n y la investigaci&oacute;n, estas &uacute;ltimas desarrolladas por el <strong>Instituto de Investigaci&oacute;n y Capacitaci&oacute;n Educativa (INICE)</strong>.',null);
