Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1266','0','¿Qué contiene?','Proporciona datos estad&iacute;sticos, publicaciones, campa&ntilde;as informativas, documentos, acciones de formaci&oacute;n, links, an&aacute;lisis de medios de comunicaci&oacute;n.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1267','0','¿Qué es?','Portal abierto al ciudadano interesado en conocer aspectos destacados del proceso de integraci&oacute;n de la poblaci&oacute;n extranjera en Espa&ntilde;a.<br />',null);
