Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('330','0','p_01','<p>Los objetivos de la Educaci&oacute;n peruana est&aacute;n orientados a:<br />&nbsp;- El desarrollo integral del educando y su personalidad, de tal manera que le permita el conocimiento de sus deberes y derechos que lo capacite para su vida en la sociedad.<br />&nbsp;- La superaci&oacute;n del analfabetismo.<br />&nbsp;-Alcanzar una mayor integraci&oacute;n cultural latinoamericana.<br />&nbsp;- Construir una sociedad m&aacute;s democr&aacute;tica donde todos los ciudadanos gocen de igualdad de derechos.</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3405','0',null,'&nbsp;&nbsp;&nbsp; En 2003, la ministra federal estableci&oacute; la Zukunftskommission (Comisi&oacute;n del Futuro) para garantizar y aumentar la calidad de la ense&ntilde;anza en los centros escolares y para confeccionar un cat&aacute;logo de medidas de reforma orientadas a objetivos precisos. Este plan contiene siete &aacute;reas de acci&oacute;n y 30 medidas espec&iacute;ficas ampliamente discutidas.<br />&nbsp;&nbsp;&nbsp; En febrero de 2005 tuvo lugar un coloquio para la reforma de la educaci&oacute;n en donde miembros del gobierno debatieron diversas propuestas de reforma; en &eacute;l se trataron diferentes cuestiones que se circunscriben a tres parcelas principales:<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &bull;&nbsp;adecuaci&oacute;n al mundo laboral moderno,<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &bull;&nbsp;mayor orientaci&oacute;n a las necesidades de los estudiantes,<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &bull;&nbsp;profesionalizaci&oacute;n de la docencia.<br /><br />&nbsp;&nbsp;&nbsp; En el curso 2008/09 se impuls&oacute; el proyecto de la Nueva educaci&oacute;n Secundaria, que tiene por objetivo crear un ciclo conjunto de los 9 a los 12 a&ntilde;os, y por lo tanto reestructurar la estructura de la educaci&oacute;n obligatoria. Este proyecto, adem&aacute;s de evitar la separaci&oacute;n prematura de los ni&ntilde;os en las diferentes v&iacute;as educativas, se implementa con una nueva cultura del aprendizaje basada en la individualizaci&oacute;n y diferenciaci&oacute;n interna, apoy&aacute;ndose en el propio ritmo de cada alumno. A ra&iacute;z del &eacute;xito desde su lanzamiento, en el curso 2009/10 lo desarrollaron un total de 244 centros. Por otro lado, y con el objetivo de reducir la tasa de abandono escolar temprano, durante el curso 2008/09 se implant&oacute; la ense&ntilde;anza en grupos peque&ntilde;os. <br />',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1177','0','p_02','Los objetivos de la Educaci&oacute;n Primaria est&aacute;n orientados a:<br />&nbsp;- Alcanzar un adecuado nivel lector y buena capacidad de lenguaje, tanto escrito como oral como matem&aacute;tico, adem&aacute;s de un conocimiento b&aacute;sico de la historia y geograf&iacute;a, y fen&oacute;menos de la naturaleza.<br />&nbsp;- Desarrollar al educando a nivel cognitivo, f&iacute;sico y creativo.<br />&nbsp;- Orientar el desarrollo.<br />&nbsp;- Promover el conocimiento y pr&aacute;ctica de los valores culturales.',null);
