Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('353','0','p_01','Los objetivos que persigue la Educaci&oacute;n son:<br />&nbsp;- Promover el desarrollo personal y social de los ni&ntilde;os con base en la experiencia de la vida democr&aacute;tica, en una perspectiva de educaci&oacute;n para la ciudadan&iacute;a.<br />&nbsp;- Fomentar la inserci&oacute;n del ni&ntilde;o en grupos sociales diversos, en el respeto por la pluralidad de culturas, favoreciendo una progresiva conciencia de su papel como miembro de la comunidad.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('354','0','p_01','El sistema educativo portugu&eacute;s ofrece la escolarizaci&oacute;n en la modalidad de Educaci&oacute;n Especial, impartida normalmente en centros educativos ordinarios, con estructuras de apoyo locales. En el curso 2002/03, la cifra de alumnado escolarizado en esta modalidad educativa ascend&iacute;a a una ratio de nueve alumnos/ as por docente.',null);
