Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1278','0','Qué es','Parte Hartuz es un grupo consolidado de investigaci&oacute;n y formaci&oacute;n en democracia participativa integrado por personas miembros &#8213;profesores, investigadores, becarios y colaboradores&#8213; de los departamentos de Ciencia Pol&iacute;tica, Sociolog&iacute;a y Educaci&oacute;n de la Universidad del Pa&iacute;s Vasco &ndash; Euskal Herriko Unibertsitatea. Parte Hartuz es tambi&eacute;n una asociaci&oacute;n que impulsa procesos y experiencias de democracia participativa en el Pa&iacute;s Vasco. 1) Posibilitar procesos de participaci&oacute;n en el ejercicio del poder &#8213;reparto del poder&#8213;, entendiendo el poder como un elemento relacional y conflictivo de la vida social y pol&iacute;tica; 2) Establecer redes autogestionables; 3) Impulsar procesos participativos plenos, incidiendo en la capacidad de decidir, ejecutar y evaluar; 4) Promover el protagonismo social y el papel de las organizaciones sociales y los ciudadanos no organizados en el funcionamiento democr&aacute;tico; 5) Impulsar la participaci&oacute;n y en consecuencia el empoderamiento interno, tambi&eacute;n dentro de las organizaciones y movimientos sociales.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1273','0','Idiomas','Castellano, catal&aacute;n, ingl&eacute;s, franc&eacute;s y portugu&eacute;s.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1274','0','Qué contiene','Un Banco de experiencias donde se recogen las experiencias innovadoras en el campo de la democracia participativa llevadas a cabo por los miembros del OIDP.Tambi&eacute;n ofrece una secci&oacute;n de agenda, noticias, links de inter&eacute;s y estudios sobre democracia participativa y una secci&oacute;n de subvenciones donde se informa de premios y convocatorias dirigidas a los gobiernos locales.<br />',null);
