Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1280','0','enlace homologacion no universitaria','<p>La siguiente direcci&oacute;n remite a la informaci&oacute;n que suministra el&nbsp;Ministerio de Educaci&oacute;n&nbsp;relativa a:&nbsp;homologaci&oacute;n y convalidaci&oacute;n de estudios de Sistemas Educativos Extranjeros a sus equivalentes espa&ntilde;oles de educaci&oacute;n no universitaria, normativa, &oacute;rganos competentes, documentaci&oacute;n, solicitudes...</p>',null);
