Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('425','0','Qué contiene','Base de datos que incluye cinco secciones: <br />1. Materiales: propuestas pedag&oacute;gicas en diversos formatos (pdf, word, p&aacute;ginas web) organizadas en torno a cuatro temas: educaci&oacute;n intercultural, educaci&oacute;n para el desarrollo, educaci&oacute;n en valores y educaci&oacute;n para la paz (&eacute;sta &uacute;ltima con tres subtemas: paz, derechos humanos y resoluci&oacute;n de conflictos y mediaci&oacute;n). Destaca el apartado dedicado al cine como recurso, en el que se ofrece una selecci&oacute;n de pel&iacute;culas que incluye ficha t&eacute;cnica, gu&iacute;a did&aacute;ctica y propuestas de actividades.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('426','0','Qué es','Red de recursos en Educación para la paz, el desarrollo y la interculturalidad. Creada por iniciativa conjunta de Pangea, Sedupaz y Sodepau, cuenta con el apoyo de la Generalitat de Cataluña y los Ayuntamientos de Barcelona, Córdoba y Granollers, así como con la colaboración de diversas asociaciones y fundaciones.',null);
