Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('429','0','Qué contiene','La web se organiza en cinco secciones:<br />- Documentos del proyecto, incluyendo un modelo de colaboraci&oacute;n con los centros que se incorporen.<br />- Materiales curriculares aportados por los miembros del colectivo.<br />- Experiencias de cambio o mejora de los centros implicados.<br />- Recursos, que incluye enlaces y el foro &ldquo;la cafeter&iacute;a&rdquo;.<br />- Informaci&oacute;n sobre convocatorias, novedades bibliogr&aacute;ficas, etc.<br />',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('360','0','Inglaterra y Gales','La educaci&oacute;n en <strong>Inglaterra</strong>, en el <strong>Pa&iacute;s de Gales</strong> y en <strong>Irlanda del Norte</strong> es ampliamente similar. El sistema educativo en <strong>Escocia</strong>, sin embargo, es y ha sido siempre totalmente diferente, desarrollado con sus propias leyes y pr&aacute;cticas.&nbsp;<br />La educaci&oacute;n es la prioridad principal del Gobierno del Reino Unido.&nbsp;<br />En <strong>Inglaterra</strong>, el nuevo Departamento de Infancia, Centros Docentes y Familia dirige sus actuaciones para que todos los ni&ntilde;os y j&oacute;venes:&nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - Tengan seguridad y salud&nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - Accedan a una educaci&oacute;n excelente y a est&aacute;ndares de rendimiento lo m&aacute;s altos posible&nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - Disfruten de su infancia&nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - Hagan una contribuci&oacute;n positiva a la sociedad y a la econom&iacute;a.<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;- Tengan en las mismas oportunidades, independientemente de sus condiciones econ&oacute;micas.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('807','0','Qué contiene','El portal ofrece una cantidad ingente de informaci&oacute;n y recursos, rese&ntilde;ados y analizados, organizados en torno a diversos n&uacute;cleos tem&aacute;ticos: comunicaci&oacute;n, cooperaci&oacute;n, culturas, ense&ntilde;anza de segundas lenguas, g&eacute;nero, globalizaci&oacute;n, poblaciones ind&iacute;genas, racismo y xenofobia, etc.<br />',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('808','0','Qué es','Iniciativa sostenida por la Federaci&oacute;n de Trabajadores de la Ense&ntilde;anza, FETE-UGT, con el apoyo de la Direcci&oacute;n General de Integraci&oacute;n de los Inmigrantes y del Instituto de Formaci&oacute;n del profesorado, Investigaci&oacute;n e Innovaci&oacute;n educativa, IFIIE. Su objetivo es proporcionar informaci&oacute;n y facilitar canales de comunicaci&oacute;n e intercambio entre educadores con el fin de avanzar en una educaci&oacute;n en pos del respeto y la convivencia entre las diferentes culturas.',null);
