Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('438','0','Qué es','Iniciativa de Hegoa, una organización sin ánimo de lucro que trabaja en la promoción del desarrollo humano sostenible de los pueblos. Bantaba trata de ser un punto de encuentro en el que personas y colectivos tengan la oportunidad de comunicarse e intercambiar (a través de metodologías, herramientas y lenguajes nuevos) experiencias y aprendizajes que nos permitan transformar nuestro entorno en el camino hacia un desarrollo más humano.',null);
