Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('442','0','Qué es','<span style="FONT-SIZE: 10pt; FONT-FAMILY: Calibri">La<strong> Fundaci&oacute;n Desarrollo</strong></span><strong><span style="FONT-SIZE: 10pt; FONT-FAMILY: Calibri"> Sostenido</span></strong><span style="FONT-SIZE: 10pt; FONT-FAMILY: Calibri">, <strong>Fundeso</strong>, es una ONG aconfesional, apol&iacute;tica e independiente, fundada por Rafael Guardans Camb&oacute; en 1995. </span><span style="FONT-SIZE: 10pt; FONT-FAMILY: Calibri">En casi quince a&ntilde;os y de la mano de un buen n&uacute;mero de donantes privados y p&uacute;blicos, Fundeso ha destinado m&aacute;s de 40 millones de euros para proyectos de desarrollo integral y sostenible en 26 pa&iacute;ses de Am&eacute;rica Latina, &Aacute;frica y Asia.<br /></span><span style="FONT-SIZE: 10pt; FONT-FAMILY: Calibri">Asimismo ha promovido&nbsp; proyectos de educaci&oacute;n y sensibilizaci&oacute;n de la sociedad espa&ntilde;ola sobre asuntos relacionados con el desarrollo, e impulsado programas de atenci&oacute;n directa a inmigrantes.<p>&nbsp;</p></span>',null);
