Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('449','0','Qué contiene','La web es b&aacute;sicamente informativa. Se estructura en seis secciones, entre las que destacamos tres:<br />- Qui&eacute;nes somos. Ofrece informaci&oacute;n sobre la organizaci&oacute;n, su carta de principios, actividades y localizaci&oacute;n.',null);
