Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('465','0','Qué contiene','La página es de carácter informativo sobre las actividades, grupos de trabajo, proyectos, publicaciones y servicios (entre ellos, la Biblioteca, cuyos catálogos pueden consultarse en línea) de la Asociación. Ofrece la posibilidad de consultar los sumarios de las revistas editadas por la Asociación: &#8220;Infancia&#8221; y &#8220;Perspectiva Escolar&#8221;. Ofrece igualmente un espacio para la participación a través del Foro y de la sección dedicada a Debates abiertos.<br>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('466','0','Qué es','Asociación catalana de maestros y educadores que trabajan por la calidad de la enseñanza. Constituyen el Movimiento de Renovación Pedagógica de Cataluña y son al mismo tiempo un Centro de Servicios Educativos.',null);
