Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('469','0','Qué contiene','Información sobre la propia asociación: objetivos, historia, colectivos asociados, documentos base en torno a la educación intercultural y la superación de desigualdades. Destaca la sección del Centro de Documentación, en la que es posible consultar a texto completo los boletines editados por la AECGIT, y obtener información sobre recursos, experiencias, bibliografía y materiales.<br>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('470','0','Qué es','La Asociación de Enseñantes con Gitanos es una Asociación no gubernamental, civil, aconfesional, pluriétnica, intercultural y apartidista, de carácter socio-educativo y de funcionamiento democrático. Pretende contribuir a la desaparición de todo tipo de actitudes negativas, discriminación, intolerancia y racismo, y crear un ambiente de convivencia e interrelación cultural entre las múltiples culturas que conforman nuestra sociedad.',null);
