Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('766','0','Paso 4','Seleccionamos la carpeta que contiene las nuevas fuentes que&nbsp;deseamos instalar,&nbsp;&nbsp; pulsando sobre la tecla Ctrl para escoger m&aacute;s de una a la vez. Nos aseguramos de que la opci&oacute;n &quot;Copiar fuentes a la carpeta Fuentes&quot; est&eacute; marcada y damos a aceptar. Las fuentes seleleccionadas se instalar&aacute;n entonces&nbsp;en&nbsp;el ordenador.<br /><br />',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('767','0','Final','Para obtener m&aacute;s detalles sobre la instalaci&oacute;n podemos acudir al <a href="http://windows%20update%20website/" target="_new">Soporte&nbsp;T&eacute;cnico&nbsp;de&nbsp;Microsoft</a>',null);
