Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('832','0','p_02','Entre sus actividades se cuentan la promoci&oacute;n de investigaciones, la elaboraci&oacute;n de propuestas, la organizaci&oacute;n de encuentros y jornadas de debate, el mantenimiento de un centro de documentaci&oacute;n y diversas acciones reivindicativas.<br />',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('833','0','p_03','- Documentaci&oacute;n, con art&iacute;culos y documentos diversos a texto completo.<br />- Legislaci&oacute;n nacional y de la Comunidad de Madrid.<br />- Acciones, con informaci&oacute;n sobre convocatorias pasadas y futuras.<br />- Enlaces organizados sistem&aacute;ticamente.<br />Se ofrece adem&aacute;s la posibilidad de inscribirse en el movimiento y recibir informaci&oacute;n actualizada sobre novedades.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('837','0','p_04','- Formarse f&aacute;cil. Propuestas did&aacute;cticas disponibles con indicaciones metodol&oacute;gicas y todos los recursos necesarios (esquemas, presentaciones, textos, din&aacute;micas, bibliograf&iacute;as&hellip;) para formarse en grupos y/o personalmente sobre temas relacionados con el desarrollo humano, g&eacute;nero, interculturalidad, educaci&oacute;n para el desarrollo, comunicaci&oacute;n, seguridad alimentaria, cooperaci&oacute;n internacional, etc.',null);
