Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('798','0','p08','<strong><br />Windows 98, Windows ME, Windows NT 4.0 y Windows 95<br /><br /></strong>Ninguno de estos productos incluye la fuente Arial Unicode MS, por lo que es necesario instalar una fuente alternativa que permita visualizar caracteres en chino.<br /><br />Es posible descargarse fuentes gratuitas a trav&eacute;s de Internet, como <a href="http://cerg1.ugc.edu.hk/cergprod/static/download_font.jsp" target="_new"><font color="#3366ff">CERG Chisese Font</font></a><font color="#3366ff">.</font> Lea las licencias incluidas antes de proceder a la instalaci&oacute;n.&nbsp;<br /><br />',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('836','0','p_05','- Laboratorio educativo. Espacio en el que se pueden encontrar documentos a texto completo relacionados con la Educaci&oacute;n para el Desarrollo y la Solidaridad; referencias de recursos did&aacute;cticos (obras de referencia, bibliograf&iacute;a, Cd Rom, v&iacute;deos y gu&iacute;as did&aacute;cticas); enlaces a otras p&aacute;ginas web y acceso on-line al cat&aacute;logo del CEREC (Centro de Recursos Did&aacute;cticos) de Hegoa en Vitoria-Gasteiz; y un espacio para el intercambio y la comunicaci&oacute;n de experiencias de centros educativos.',null);
