Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1305','0','Qué contiene','Diversas &aacute;reas de trabajo: Derechos Ciudadanos, Participaci&oacute;n Electoral, Incidencia en las Pol&iacute;ticas P&uacute;blicas y Evaluaci&oacute;n y monitoreo. Se presentan los proyectos que se llevan entre manos, las publicaciones y un listado de enlaces de inter&eacute;s.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1306','0','Qué es','Participa es una corporaci&oacute;n privada, pluralista y no partidaria que promueve fines p&uacute;blicos. Trabaja a nivel global, regional y nacional. PARTICIPA busca mejorar la calidad de la democracia promoviendo la participaci&oacute;n ciudadana en los asuntos de inter&eacute;s p&uacute;blico y un ejercicio responsable del gobierno. Nuestro prop&oacute;sito es contribuir para que los valores y derechos democr&aacute;ticos sean conocidos, respetados y ejercidos por ciudadanos informados, organizados y participativos. Tambi&eacute;n abogamos por pr&aacute;cticas transparentes y por la rendici&oacute;n de cuentas en la gesti&oacute;n p&uacute;blica.',null);
