Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1131','0','p_02','<p><br />La informaci&oacute;n se ha estructurado en&nbsp;once subsecciones:<br /><br />-&nbsp;Interculturalidad&nbsp; y&nbsp; educaci&oacute;n. <br />-&nbsp;Migraciones<br />-&nbsp;Paz, cooperaci&oacute;n y&nbsp; desarrollo<br />-&nbsp;Democracia y participaci&oacute;n<br />-&nbsp;Derechos humanos<br />-&nbsp;G&eacute;nero y diversidad<br />- Mediaci&oacute;n<br />-&nbsp;Informaci&oacute;n solidaria y alternativa<br />-&nbsp;Instituciones y organizaciones nacionales e internacionales<br />-&nbsp;Fuentes de informaci&oacute;n en educaci&oacute;n<br />- Cuentos y narrativa oral</p><p>Si tu organizaci&oacute;n ofrece contenidos en la Red similares a los descritos y no ha sido ya incluida en nuestro mapa, por favor, contacta.</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1134','0','p_05','Un cuerpo de inspectores t&eacute;cnicos, sometidos al Ministerio, funciona a nivel tanto nacional como regional, y supervisa el sistema educativo en su conjunto.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1135','0','p_04','La reorganizaci&oacute;n del Ministerio de Educaci&oacute;n, Universidad e Investigaci&oacute;n, as&iacute; como la descentralizaci&oacute;n de las responsabilidades directivas y administrativas del Estado a las autoridades locales y territoriales (regiones, provincias y municipios), pretende aumentar la eficacia tanto de los componentes centrales (Ministerio de Educaci&oacute;n) como de los perif&eacute;ricos (administraciones escolares regionales y delegaciones provinciales de Educaci&oacute;n &ndash; <em>Uffici scolastici regionali</em>) de la administraci&oacute;n escolar.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1136','0','p_03','Desde el curso escolar 2000/01, todos los centros disponen de autonom&iacute;a en lo que se refiere a la administraci&oacute;n, la organizaci&oacute;n, la pedagog&iacute;a, la investigaci&oacute;n, la experimentaci&oacute;n y el desarrollo.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1309','0','Qué es','Pretende analizar las posibilidades de utilizaci&oacute;n de Internet para apoyar y promover la participaci&oacute;n ciudadana en el &aacute;mbito municipal en pa&iacute;ses como Brasil. M&aacute;s concretamente, pretende delimitar cu&aacute;les son los requisitos que debe cumplir un Sistema Inform&aacute;tico para la promoci&oacute;n de la Participaci&oacute;n Ciudadana municipal y el fomento de las redes ciudadanas (SIPC), tomando como punto de partida las necesidades identificadas en Fortaleza, capital del Estado de Cear&aacute;. La investigaci&oacute;n proporcionar&aacute; una especificaci&oacute;n formal y detallada de tal sistema, que podr&iacute;a posteriormente utilizarse para construirlo siguiendo el modelo de Software Libre, de forma que quede a disposici&oacute;n gratuita de todos los municipios que precisen usarlo.',null);
