Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1314','0','Qué contiene','Formaci&oacute;n e Informaci&oacute;n distribuida en diversas secciones como &ldquo;eCiudadan&iacute;a&rdquo;, &ldquo;ePol&iacute;tica&rdquo;, &ldquo;eGobierno&rdquo;, &ldquo;eMedia&rdquo;, &ldquo;eUropa&rdquo;, en formato de noticias, jornadas, boletines.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1315','0','Qué es ','Es una organizaci&oacute;n surgida en el a&ntilde;o 2003, que desarrolla actividades presenciales y se estructura en un espacio virtual configurado como un think tank de conocimiento, que utiliza las tecnolog&iacute;as abiertas XML especialmente capacitadas para el tratamiento de bases documentales. Una web de referencia pol&iacute;tica y med&aacute;tica sobre la democracia digital, con capacidad para emitir opini&oacute;n y prestar servicios. Una iniciativa plural y abierta a la colaboraci&oacute;n a trav&eacute;s de su Red de expertos. Se propone reflexionar sobre los sistemas democr&aacute;ticos y la profundizaci&oacute;n de la democracia y sobre la reforma del sistema electoral y los procesos de participaci&oacute;n ciudadana, sobre la acci&oacute;n, la participaci&oacute;n y la representaci&oacute;n pol&iacute;tica. Todo ello en el marco de las nuevas posibilidades que ofrecen las Nuevas Tecnolog&iacute;as para lo que se denomina, ciberdemocracia, democracia digital o eDemocracia.',null);
