Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1328','0','Qué es','<p>Civic Global Technology Network es una organizaci&oacute;n de compa&ntilde;&iacute;as, proyectos y colaboradores que proveen de innovaci&oacute;n tecnol&oacute;gica para la acci&oacute;n c&iacute;vica en el &aacute;mbito de&nbsp;corporaciones, compa&ntilde;&iacute;as, asociaciones y organizaciones del sector p&uacute;blico.&nbsp;</p>',null);
