Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1456','0','Qué contiene','Legislaci&oacute;n vigente en materia de inmigraci&oacute;n (reglamento de extranjer&iacute;a, itinerario tipo de solicitud para el proceso de normalizaci&oacute;n) , valoraciones desde la pespectiva sindical del Reglameto de Extanjer&iacute;a, declaraci&oacute;n sindical por la Dignidad de los trabajadores inmigrantes y como tema de actualiadad propuestas para la mejora de la calidad de la educaci&oacute;n a los hijos de familias inmigrantes.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1457','0','Qué es','&Aacute;rea de trabajo de CCOO destinada a la divulgaci&oacute;n de todo tipo de informaci&oacute;n que pueda ser &uacute;til a los trabajadores inmigrantes. Estas acciones se llevan a cabo a trav&eacute;s del CITE ( Centro de Informaci&oacute;n y Asesoramiento a Trabajadores Inmigrantes).',null);
