Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1453','0','Qué contiene','&Iacute;ndice de actividades, noticias, l&iacute;neas de investigaci&oacute;n, publicaciones y proyectos de investigaci&oacute;n, cursos de postgrado ,cursos de doctorado, foro, documentos multimedia y enlaces de inter&eacute;s.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1454','0','Qué es','El GECIM es un&nbsp; grupo multidisciplinar de estudios formado por abogados, soci&oacute;logos, antrop&oacute;logos de la Universidad de Valencia que realiza investigaciones sobre ciudadan&iacute;a, inmigraci&oacute;n, multiculturalismo y minor&iacute;as as&iacute; como actividades docentes, de divulgaci&oacute;n, formaci&oacute;n, asesoramiento y sensibilizaci&oacute;n social.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2418','112',null,'<p>A aprendizaxe &eacute; parte da nosa natureza humana como o comer ou o dormir, aprendemos do contorno e das nosas experiencias constantemente. Non s&oacute; temos esta capacidade desde pequenos sen&oacute;n que temos a necesidade de aprender. Polo tanto entendemos a educaci&oacute;n como o proceso de aprendizaxe constante de todo ser humano ao longo de toda a vida.<br /><br />Un proceso constante que se produce en distintos contextos: educaci&oacute;n formal, non-formal e informal. Nos que a intencionalidade e a planificaci&oacute;n das aprendizaxes var&iacute;a desde unha gran estruturaci&oacute;n ata a non estruturaci&oacute;n. Ademais de transmitir co&ntilde;ecementos, nos procesos&nbsp;educativos&nbsp;apr&eacute;ndense valores que poden ser artellados ou permanecer impl&iacute;citos.<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>',null);
