Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1462','0','Qué contiene','Incluye direcciones de inter&eacute;s para trabajadores inmigrantes, una gu&iacute;a para trabajadores inmigrantes e integraci&oacute;n social, informaci&oacute;n sobre varias campa&ntilde;as y proyectos emprendidos por UGT, asesoramiento laboral y profesional para inmigrantes.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1463','0','Qué es','&Aacute;rea de informaci&oacute;n de la P&aacute;gina principal de UGT en la que se accede a distintos recursos informativos y orientativos para la integraci&oacute;n social y laboral de la poblaci&oacute;n inmigrante en Espa&ntilde;a (direcciones de inter&eacute;s, enlaces al INEM).',null);
