Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1469','0','Qué es','Es un proyecto encaminado a combatir contra el racismo y la xenofobia mediante acciones que faciliten la integraci&oacute;n propiciando la inserci&oacute;n en el mercado laboral de los inmigrantes.',null);
