Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1471','0','Qué contiene','Relaci&oacute;n de todas las actividades, programas y l&iacute;neas de trabajo; publicaciones, resoluciones internacionales, recopilaci&oacute;n de eventos y noticias de actualidad en materia de inmigraci&oacute;n.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1472','0','Qué es','Organizaci&oacute;n internacional creada en 1951 para representar a refugiados, personas desplazadas y migrantes europeos. Con oficinas y operaciones en cada continente la OIM&nbsp; desarrolla una amplia variedad de actividades de gesti&oacute;n de la migraci&oacute;n en todo el mundo.',null);
