Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1474','0','Qué contiene','Exposici&oacute;n de objetivos, programas y actividades, publicaciones y base de datos sobre migraciones laborales internacionales.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1475','0','Qué es','La organizaci&oacute;n Internacional del Trabajo (1919) es un organismo especializado de las Naciones Unidas que procura fomentar la justicia social y los derechos humanos y laborales. La OIT formula normas internacionales de trabajo por las que se fijan unas condiciones m&iacute;nimas en materia de derechos laborales fundamentales. La secci&oacute;n especializada en migraciones tiene como objetivo: proteger los derechos de los inmigrantes y promocionar su integraci&oacute;n y mejorar el conocimiento existente sobre las migraciones.',null);
