Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1482','0','Qué contiene','Art&iacute;culos, notas de prensa, una circular, un glosario de t&eacute;rminos, acceso a boletines de ONG, bases de datos, estad&iacute;sticas y mapas, y comentarios sobre libros y pel&iacute;culas.',null);
