Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1426','0','Qué contiene','Servicio de informaci&oacute;n en l&iacute;nea (prensa, televisi&oacute;n, agenda, correo...); estad&iacute;sticas; normativa y legislaci&oacute;n; servicio de empleo y becas; publicaciones y recursos documentales editados; secciones de trabajo: educaci&oacute;n, cultura, informaci&oacute;n y comunicaci&oacute;n, ciencias sociales y humanas y ciencias naturales. Informaci&oacute;n de cada &aacute;rea por pa&iacute;ses.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1427','0','Qué es','La Organizaci&oacute;n de las Naciones Unidas para la Educaci&oacute;n, la Ciencia y la Cultura (UNESCO) naci&oacute; el 16 de noviembre de 1945. Lo m&aacute;s importante para este organismo de las Naciones Unidas es construir la paz en la mente de los hombres mediante la educaci&oacute;n, la cultura, las ciencias naturales y sociales y la comunicaci&oacute;n.',null);
