Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1335','0','Qué es','Pagina Web de la Marcha Mundial de las Mujeres. La Carta Mundial de las Mujeres para la Humanidad propone construir un mundo sin explotaci&oacute;n, opresi&oacute;n, intolerancia y exclusiones, donde se respetan la integridad, la diversidad, los derechos y libertades de todas y todos, un mundo basado sobre la igualdad, la libertad, la solidaridad y la paz.',null);
