Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1337','0','Qué contiene','GEM es una gu&iacute;a disponible en l&iacute;nea para evaluar con perspectiva de g&eacute;nero las iniciativas que utilizan las nuevas tecnolog&iacute;as de la informaci&oacute;n y de la comunicaci&oacute;n para el cambio social. GEM provee herramientas para determinar si las TIC est&aacute;n realmente mejorando la vida de las mujeres y las relaciones de g&eacute;nero y promoviendo cambios positivos. Existe un grupo de facilitadores y consultores experimentados en GEM en Asia, Africa, Am&eacute;rica Latina y la Uni&oacute;n Europea que ofrecen sus servicios y su apoyo a las personas que deseen utilizar esta herramienta. Esta gu&iacute;a es gratuita y puede accederse a ella libremente, incluso bajarla a la computadora.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1338','0','Qué es','El Programa de Apoyo a las Redes de Mujeres de la Asociaci&oacute;n para el Progreso de las Comunicaciones ofrece la herramienta GEM como parte de su trabajo a favor del aprendizaje de las TIC y del trabajo por el avance de los derechos de las mujeres',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1339','0','Idiomas','Espa&ntilde;ol, &aacute;rabe, franc&eacute;s, ingl&eacute;s',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1340','0','Qué contiene','Noticias referentes 16 pa&iacute;ses mediterr&aacute;neos en relaci&oacute;n a temas como: inmigraci&oacute;n, feminismo, cultura, reforma del mundo &aacute;rabe, derechos humanos, religi&oacute;n, cooperaci&oacute;n, campa&ntilde;as... Dispone de un espacio privado y un &aacute;rea p&uacute;blica.',null);
