Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1352','0','Qué contiene','Informaci&oacute;n de proyectos y formaci&oacute;n para mujeres en el sector de la radio, calendario de actividades, bolet&iacute;n, directorio de radios comunitarias de mujeres...&nbsp;',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1353','0','Qué es','AMARC es una organizaci&oacute;n no gubernamental internacional al servicio del movimiento de la radio comunitaria, que agrupa cerca de 3.000 miembros y asociados en 110 pa&iacute;ses. Su objetivo es apoyar y contribuir al desarrollo de la radio comunitaria y participativa de acuerdo con los principios de solidaridad y la cooperaci&oacute;n internacional. En Dubl&iacute;n en 1990, un grupo de mujeres propuso la creaci&oacute;n de una red internacional de las mujeres trabajadoras en el sector de la radio comunitaria. No obstante, debi&oacute; esperarse hasta la Asamblea de Oaxtepec en M&eacute;xico, en el a&ntilde;o de 1992, para que la Red Internacional de Mujeres fuera creada.',null);
