Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1356','0','Qué es','La Fundaci&oacute;n Mujeres es una Organizaci&oacute;n No Gubernamental especializada en el campo de la igualdad de oportunidades entre hombres y mujeres, que lleva trabajando desde el a&ntilde;o 1994.',null);
