Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1392','0','Qué contiene','<p>Gu&iacute;a Injuve, directorio de enlaces, convocatorias (premios, concursos, encuentros,...), legislaci&oacute;n e informaci&oacute;n sobre diferentes &aacute;reas tem&aacute;ticas: ocio y tiempo libre, vivienda y empleo, sociedad y salud, estudios y publicaciones, cooperaci&oacute;n internacional, escuela de formaci&oacute;n... </p>',null);
