Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2086','0','P_06','<p>Junto a la labor de recogida y sistematizaci&oacute;n de la informaci&oacute;n y los recursos existentes en torno a la interculturalidad, el CREADE se propone desarrollar tres l&iacute;neas de actuaci&oacute;n importantes:</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2085','0','P_07','<p>-&nbsp;Mantenimiento de la l&iacute;nea de investigaci&oacute;n sobre educaci&oacute;n intercultural, (premios, desarrollo de investigaciones) y la creaci&oacute;n de un Gabinete de estudios (detecci&oacute;n y an&aacute;lisis de buenas pr&aacute;cticas educativas, estudios de casos, an&aacute;lisis de legislaci&oacute;n, an&aacute;lisis de pol&iacute;ticas educativas), </p>',null);
