Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1782','0','Qué contiene','<p>The web page is divided into the following sections: News and events, Publications, Letigation, Research and Policy, Legal Commentary, Legal Databases, Advocacy, Human rigths training, Special projects, Employment opportunities, Databases and Roma Rights Quarterly.</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2299','0',null,'<p>Es un plan cuatrienal que desarrolla la Conseller&iacute;a de Cultura, Educaci&oacute;n y Deporte para aplicar en la Comunidad Valenciana la Ley Org&aacute;nica de Calidad de la Ense&ntilde;anza (LOCE). Incluye un apartado de &quot;atenci&oacute;n a la diversidad, educaci&oacute;n intercultural e integraci&oacute;n social&quot; y uno de &quot;educaci&oacute;n pluriling&uuml;e&quot;.</p>',null);
