Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1593','0','p_01','<strong>Ilustre Colegio de Abogados de Madrid</strong> (Servicio de Orientaci&oacute;n jur&iacute;dica para extranjeros)<br />Funci&oacute;n: orientaci&oacute;n jur&iacute;dica a extranjeros, en relaci&oacute;n con la aplicaci&oacute;n de la Ley de Extranjer&iacute;a y Ley de Asilo y Refugio. Sede: C/ General Casta&ntilde;os 1 (Tribunal Superior de Justicia). Horario: 9:30 a 13:30, de lunes a viernes. El servicio tambi&eacute;n se presta en el Ayuntamiento de Torrej&oacute;n de Ardoz, de 17:00 a 19:00, los lunes y jueves (C/ Virgen de Loreto N&ordm; 2).',null);
