Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1687','0','Qué contiene','En la p&aacute;gina web de este instituto se presentan art&iacute;culos de opini&oacute;n que analizan diversos aspectos de la sociedad y pol&iacute;tica actuales, agrupados bajo los ep&iacute;grafes de &quot;acci&oacute;n humanitaria&quot;, &quot;conflictos&quot;, &quot;derechos humanos&quot;, &quot;desarrollo&quot;, &quot;migraciones&quot;, &quot;narcotr&aacute;fico&quot; y &quot;seguridad&quot;, o seg&uacute;n su localizaci&oacute;n geogr&aacute;fica (Am&eacute;rica Latina, &Aacute;frica Subsahariana, Asia Central, Balcanes, Oriente Medio y Norte de &Aacute;frica). Hay tambi&eacute;n un apartado de crisis y atentados. Adem&aacute;s, se incluyen links a centros relacionados e informaci&oacute;n sobre recursos.',null);
