Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1690','0','Qué contiene','Este portal contiene una informaci&oacute;n muy abundante sobre diversos aspectos relacionados con el desarrollo, organizados por temas (mujeres y g&eacute;nero, salud, econom&iacute;a, etc.) o localizaci&oacute;n geogr&aacute;fica, dando acceso a documentos descargables en formato pdf o html. Incluye la revista GloobalHoy que proporciona informaci&oacute;n y an&aacute;lisis sobre la situaci&oacute;n social y pol&iacute;tica mundial, centr&aacute;ndose en el llamado Tercer Mundo y, prioritariamente, en Am&eacute;rica Latina. Contiene tambi&eacute;n &iacute;ndices con informaci&oacute;n sobre: organizaciones, revistas tem&aacute;ticas con contenidos globales y locales, redes sociales, gu&iacute;as de recursos, cat&aacute;logos y un diccionario.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1691','0','Qué es','El Portal GLOOBAL &quot;La cooperaci&oacute;n internacional en red&quot; pretende hacer visible informaci&oacute;n sobre las organizaciones y movimientos sociales del siglo XXI, como recurso estrat&eacute;gico al servicio del desarrollo. Su objetivo es crear un espacio de intercambio y apoyo mutuo entre organizaciones y redes del Norte y del Sur, al servicio de los pueblos del Sur y del tejido de organizaciones, redes y movimientos sociales que trabajan por el desarrollo sostenible, la cooperaci&oacute;n, la igualdad entre g&eacute;neros, el medio ambiente, los derechos humanos... <br />',null);
