Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1698','0','Qué contiene','Proponen &quot;centros virtuales&quot; de informaci&oacute;n sobre diferentes temas (&Aacute;frica Austral, Centroam&eacute;rica y M&eacute;xico, Comercio Justo, Derechos Humanos, &Eacute;tica y Educaci&oacute;n en Valores, Magreb, Migraciones y Racismo), adem&aacute;s de esquematizar una red de informaci&oacute;n con links a bases de datos, instituciones,&nbsp; publicaciones, otros documentos, comunicaciones y ponencias, informaci&oacute;n sobre docencia e investigaci&oacute;n, bibliotecas, cat&aacute;logos de editoriales, recogida de denuncias, recursos en internet y convocatoria de actividades.',null);
