Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1748','0','Qué contiene','Materiales did&aacute;cticos sobre la cultura y la lengua espa&ntilde;ola, materiales pedag&oacute;gicos que propicien el conocimiento de otras culturas. Adem&aacute;s tenemos acceso a informaci&oacute;n sobre todos los programas, cursos, becas y actividades educativas que desarrolla la Consejer&iacute;a&nbsp; de Educaci&oacute;n en EEUU y Canad&aacute;.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1749','0','Qué es','Revista publicada por la Consejer&iacute;a de Educaci&oacute;n y Ciencia en EEUU y Canad&aacute; cuyo fin es proporcionar al profesorado unidades did&aacute;cticas sobre cultura espa&ntilde;ola. En la actualidad se presentan unidades para una ense&ntilde;anza multicultural dentro de un marco que relacione entre s&iacute; la cultura espa&ntilde;ola/europea, la estadounidense y las hisp&aacute;nicas.',null);
