Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1921','0','Qué contiene','<p>Contiene informaci&oacute;n sobre cursos, becas, publicaciones (algunos documentos completos en red) y sobre la C&aacute;tedra UNESCO de formaci&oacute;n de profesionales del desarrollo sostenible. Incluye el Bolet&iacute;n de suscripci&oacute;n de la Revista Espa&ntilde;ola de Desarrollo y Cooperaci&oacute;n y un servicio de asistencia t&eacute;cnica que proporciona formaci&oacute;n y asistencia t&eacute;cnica sobre identificaci&oacute;n, formulaci&oacute;n, evaluaci&oacute;n previa, elaboraci&oacute;n de sistemas de seguimiento y evaluaci&oacute;n posterior de proyectos de cooperaci&oacute;n internacional para el desarrollo. Adem&aacute;s da acceso a una gu&iacute;a comentada de recursos sobre evaluaci&oacute;n y participaci&oacute;n en Internet.</p>',null);
