Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1776','0','Qué contiene','<p>Noticias, casos e informes, revistas y boletines, base de datos sobre casos que han sido llevados a los tribunales y diversos documentos sobre derechos humanos relacionados con temas econ&oacute;micos, sociales, culturales, de g&eacute;nero, de libertad de expresi&oacute;n, etc.</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1777','0','Qué es','<p>El Centro Internacional para la Protecci&oacute;n Legal de Derechos Humanos trabaja por el respeto y la promoci&oacute;n de los derechos humanos. Sus objetivos son fortalecer la jurisprudencia y los mecanismos de los derechos humanos a trav&eacute;s del derecho internacional, empoderar a organismos asociados para promover y proteger los derechos humanos, asistir a abogados, jueces, ONGs y v&iacute;ctimas dise&ntilde;ando actividades que den respuesta a las necesidades de cada grupo y regi&oacute;n, y desarrollar programas en distintas regiones de todo el mundo centrados en la defensa y la libertad de los derechos humanos.</p>',null);
