Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1779','0','Qué contiene','<p>Informes, dossieres de prensa, comunicados, denuncias, unidades did&aacute;cticas, foros tem&aacute;ticos, boletines mensuales, seminarios y jornadas internacionales de Derechos Humanos celebradas cada dos a&ntilde;os.<br />Desde las distintas &aacute;reas (Inmigraci&oacute;n, C&aacute;rceles, Marginaci&oacute;n, Educaci&oacute;n y Solidaridad internacional) se elaboran documentos, propuestas y alternativas sobre temas relativos a la defensa de los derechos humanos en los m&aacute;s diversos &aacute;mbitos.<br />Campa&ntilde;as concretas como &quot;No m&aacute;s muertes en el Estrecho&quot;, &quot;Papeles para todos y todas&quot;, &quot;Manifiesto de las dos orillas&quot; y &quot;Acercamiento de presos a las c&aacute;rceles andaluzas&quot;.</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1752','0','Qué es','Senderi, Educaci&oacute; en Valors &eacute;s una xarxa de dinou institucions compromeses amb l&rsquo;educaci&oacute; en valors, que t&eacute; la voluntat de constituir un espai d&rsquo;intercanvi i de reflexi&oacute; conjunta, de difusi&oacute; d&rsquo;experi&egrave;ncies de treball i de recursos al voltant de l&rsquo;educaci&oacute;. Es tracta d&rsquo;una iniciativa pionera que a trav&eacute;s de les Tecnologies de la Informaci&oacute; i la Comunicaci&oacute; (TIC) pret&eacute;n posar en relaci&oacute; la majoria d&rsquo;entitats que treballen en l&rsquo;educaci&oacute; en valors, que tenen una &agrave;mplia representaci&oacute; social i que fan un servei a la comunitat',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1757','0','Qué contiene','Contiene actividades de aula, ejercicios y juegos tem&aacute;ticos: alimentaci&oacute;n, valores, emociones, matem&aacute;ticas, lengua, ingl&eacute;s, franc&eacute;s e italiano usando las TIC como herramienta de aprendizaje.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1754','0','Qué contiene','Encontramos los objetivos del proyecto, comunicados de la organizaci&oacute;n y actividades desarrolladas seg&uacute;n el modelo pedag&oacute;gico que aplican.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1755','0','Qué es','Es un proyecto pedag&oacute;gico y social que surgi&oacute; por iniciativa popular en 1973 en el barrio madrile&ntilde;o de Prosperidad destinado a la educaci&oacute;n de adultos. Se define como una asociaci&oacute;n independiente y aut&oacute;noma de las administraciones y de cualquier otro tipo de organizaci&oacute;n social, econ&oacute;mica , pol&iacute;tica o religiosa.',null);
