Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1761','0','Qué es','La Asociaci&oacute;n Mundial para la Escuela Instrumento de Paz desarrolla actividades relativas en la educaci&oacute;n en y para los Derechos Humanos y la Paz. La EIP contribuye activamente a sensibilizar a los &aacute;mbitos educativos, las autoridades y la opini&oacute;n p&uacute;blica acerca de la necesidad de una ense&ntilde;anza sist&eacute;mica en relaci&oacute;n con estos temas. Sus acciones se orientan a lograr la adquisici&oacute;n de conocimientos, actitudes y aptitudes relacionadas con el respeto y la promoci&oacute;n de los derechos y libertades fundamentales.',null);
