Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1723','0','Qué contiene','<p>Contiene art&iacute;culos y recursos did&aacute;cticos para&nbsp;la educaci&oacute;n:&nbsp;<br />-&nbsp;para la Comunicaci&oacute;n<br />- para los Derechos Humanos<br />- en Valores<br />- en Interculturalidad<br />- para el Desarrollo<br />- para el Medio Ambiente<br />- para la Ciudadan&iacute;a<br />- para la Convivencia<br />- para la Igualdad<br />- para la Paz<br />- para la Salud<br />- para la prevenci&oacute;n de la Violencia<br />-&nbsp;Sexual y Afectiva<br /><br /></p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1724','0','Qué es','Proyecto educativo de formaci&oacute;n en valores dirigido a ni&ntilde;os de edades comprendidas entre los 6 y los 12 a&ntilde;os. El objetivo perseguido es el de potenciar la participaci&oacute;n de los ni&ntilde;os en un mundo m&aacute;s justo, equitativo y solidario. Este proyecto ha sido concebido por la FETE-UGT(La Federaci&oacute;n de Ense&ntilde;anza de la UGT) en colaboraci&oacute;n con&nbsp; el ISCOD ( Organizaci&oacute;n no gubernamental para la cooperaci&oacute;n sindical con pa&iacute;ses en v&iacute;as de desarrollo).',null);
