Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1726','0','Qué contiene','Incluye varios apartados que contienen informaci&oacute;n sobre derechos de la infancia: derecho a la educaci&oacute;n, igualdad, nacionalidad, salud, familia, participaci&oacute;n, desarrollo de capacidades, juego, etc. Cada derecho se expone mediante una descripci&oacute;n, un cuento, un juego, una manualidad , una receta y una pel&iacute;cula.<br />Tambi&eacute;n contiene un espacio llamado &ldquo;molamazo&rdquo; destinado al aprendizaje&nbsp; de derechos a trav&eacute;s de distintos medios: animaci&oacute;n a la lectura, pel&iacute;culas, juegos, m&uacute;sica, etc., y un ciberperi&oacute;dico infantil con noticias y eventos destinados a la concienciaci&oacute;n social sobre los derechos de la infancia.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1727','0','Qué es','Portal&nbsp; creado por la Liga Espa&ntilde;ola de la Educaci&oacute;n y la Cultura Popular, ONG plural, aconfesional e independiente que pretende llevar los beneficios de la Educaci&oacute;n y la Cultura a los sectores de poblaci&oacute;n m&aacute;s vulnerables, principalmente la juventud y los ni&ntilde;os. Rayuela es una web&nbsp; destinada a los derechos de los ni&ntilde;os.',null);
