Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1732','0','Qué contiene','Por secciones:<br />-&nbsp;Propuestas de actividades y materiales para la utilizaci&oacute;n del cine como recurso educativo.<br />-&nbsp;Recusos: materiales tales como una filmoteca virtual, materiales did&aacute;cticos aplicados al cine, filmograf&iacute;as, cuadernillos de trabajo, etc.<br />-&nbsp;Foro de debate y discusi&oacute;n.<br />-&nbsp;Bibliograf&iacute;a sobre c&oacute;mo aprender de cine (cine como contenido de estudio); c&oacute;mo aprender con el cine (el cine como recurso para el aprendizaje) y como aprender del cine (el cine como motivaci&oacute;n y experiencia).',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1733','0','Qué es','Es un punto de encuentro y un cauce para facilitar a los docentes informaci&oacute;n, materiales y publicaciones relacionados con el uso did&aacute;ctico del cine, un cat&aacute;logo on- line de recursos f&iacute;lmicos de&nbsp; Centros de escolares, un canal de comunicaci&oacute;n y un espacio de intercambio de experiencias en este campo.',null);
