Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1741','0','Qué contiene','Adem&aacute;s de una descripci&oacute;n de cada uno de los programas que desde esta organizaci&oacute;n se han puesto en marcha, podemos encontrar informaci&oacute;n sobre la organizaci&oacute;n, sus miembros, noticias medioambientales relevantes y publicaciones.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('1742','0','Qué es','ONG sin &aacute;nimo de lucro&nbsp; que trabaja para promover el desarrollo sostenible a trav&eacute;s de la educaci&oacute;n ambiental. La FEE es una organizaci&oacute;n internacional que cuenta con organizaciones miembro de Europa, Norte y Sur Am&eacute;rica, &Aacute;frica, Asia y Ocean&iacute;a. Desde 1981 la FEE viene promoviendo y desarrollando la educaci&oacute;n ambiental a trav&eacute;s de sus programas internacionales; Bandera Azul, Ecoescuelas, J&oacute;venes Reporteros para el medio ambiente, Bosques en la Escuela y la Llave Verde.',null);
